import React, { useState, useEffect } from 'react';
import { Deal } from '../../types/crm/Deal';
import { Contact } from '../../types/crm/Contact';
import styles from '../../styles/crm/dealform.module.css';

interface DealFormProps {
  deal: Deal;
  contacts: Contact[];
  onSave: (deal: Deal) => void;
  onCancel: () => void;
}

const DealForm: React.FC<DealFormProps> = ({ deal, contacts, onSave, onCancel }) => {
  // Ensure that required fields have default values
  const [formData, setFormData] = useState<Deal>({
    ...deal,
    deal_name: deal.deal_name || '',
    expected_close_date: deal.expected_close_date || '',
    contact_ids: deal.contact_ids || [],
  });
  const [errors, setErrors] = useState<{ [key: string]: string }>({});

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    setErrors(prev => ({ ...prev, [name]: '' })); // Clear error when user starts typing
  };

  const handleAddContact = (contactId: string) => {
    if (contactId && !formData.contact_ids?.includes(contactId)) {
      setFormData(prev => ({
        ...prev,
        contact_ids: [...(prev.contact_ids || []), contactId],
      }));
    }
  };

  const handleRemoveContact = (contactId: string) => {
    setFormData(prev => ({
      ...prev,
      contact_ids: prev.contact_ids?.filter(id => id !== contactId),
    }));
  };

  const validateForm = () => {
    const newErrors: { [key: string]: string } = {};
    if (!formData.deal_name) newErrors.deal_name = 'Deal name is required';
    if (!formData.expected_close_date) newErrors.expected_close_date = 'Expected close date is required';
    if (!formData.contact_ids || formData.contact_ids.length === 0) newErrors.contact_ids = 'At least one contact is required';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (validateForm()) {
      onSave(formData);
    }
  };

  return (
    <div className={styles.popupOverlay}>
      <div className={styles.popup}>
        <form onSubmit={handleSubmit} className={styles.form}>
          <h3>{deal._id ? 'Edit Deal' : 'Add Deal'}</h3>
          <label>
            Deal Name:
            <input
              type="text"
              name="deal_name"
              value={formData.deal_name}
              onChange={handleChange}
              className={errors.deal_name ? styles.errorInput : ''}
            />
            {errors.deal_name && <span className={styles.errorText}>{errors.deal_name}</span>}
          </label>
          <label>
            Stage:
            <select name="stage" value={formData.stage} onChange={handleChange}>
              <option value="new">New</option>
              <option value="discovery">Discovery</option>
              <option value="proposal">Proposal</option>
              <option value="negotiation">Negotiation</option>
              <option value="won">Won</option>
              <option value="lost">Lost</option>
            </select>
          </label>
          <label>
            Deal Value:
            <input
              type="number"
              name="deal_value"
              value={formData.deal_value}
              onChange={handleChange}
            />
          </label>
          <label>
            Expected Close Date:
            <input
              type="date"
              name="expected_close_date"
              value={formData.expected_close_date}
              onChange={handleChange}
              className={errors.expected_close_date ? styles.errorInput : ''}
            />
            {errors.expected_close_date && <span className={styles.errorText}>{errors.expected_close_date}</span>}
          </label>
          <label>
            Close Probability (%):
            <input
              type="number"
              name="close_probability"
              value={formData.close_probability}
              onChange={handleChange}
            />
          </label>
          <label>
            Contacts:
            <select onChange={(e) => handleAddContact(e.target.value)}>
              <option value="">Select Contact</option>
              {contacts.map(contact => (
                <option key={contact._id} value={contact._id}>
                  {contact.contact_name}
                </option>
              ))}
            </select>
            <div>
              {formData.contact_ids?.map(id => (
                <span key={id} onClick={() => handleRemoveContact(id)}>
                  {contacts.find(c => c._id === id)?.contact_name} ✕
                </span>
              ))}
            </div>
            {errors.contact_ids && <span className={styles.errorText}>{errors.contact_ids}</span>}
          </label>

          <div className={styles.buttonGroup}>
            <button type="submit">Save</button>
            <button type="button" onClick={onCancel}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default DealForm;
