import React, { useEffect, useState } from 'react';
import { Deal } from '../../types/crm/Deal';
import DealForm from './DealForm';
import { DealService } from '../../services/dealService';
import { ContactService } from '../../services/contactService';
import { Contact } from '../../types/crm/Contact';
import styles from '../../styles/crm/dealstable.module.css';

interface DealsTableProps {
  orgId: string;
}

const DealsTable: React.FC<DealsTableProps> = ({ orgId }) => {
  const [deals, setDeals] = useState<Deal[]>([]);
  const [contacts, setContacts] = useState<Contact[]>([]);
  const [selectedDeal, setSelectedDeal] = useState<Deal | null>(null);
  const [isFormVisible, setIsFormVisible] = useState(false);

  useEffect(() => {
    const fetchDeals = async () => {
      const deals = await DealService.getAllDeals(orgId);
      setDeals(deals);
    };

    const fetchContacts = async () => {
      const contacts = await ContactService.getAllContacts(orgId);
      setContacts(contacts);
    };

    fetchDeals();
    fetchContacts();
  }, [orgId]);

  const handleAddClick = () => {
    setSelectedDeal({
      deal_name: "",
      stage: "new",
      deal_value: 0,
      expected_close_date: "",
      close_probability: 0,
      forecast_value: 0,
      contact_ids: [],
      org_id: orgId,
    });
    setIsFormVisible(true);
  };

  const handleEditClick = (deal: Deal) => {
    setSelectedDeal(deal);
    setIsFormVisible(true);
  };

  const handleDeleteClick = async (id: string) => {
    if (window.confirm("Are you sure you want to delete this deal?")) {
      await DealService.deleteDeal(id);
      setDeals(deals.filter(deal => deal._id !== id));
    }
  };

  const handleSaveDeal = async (deal: Deal) => {
    if (deal._id) {
      await DealService.updateDeal(deal._id, deal);
      setDeals(deals.map(d => (d._id === deal._id ? deal : d)));
    } else {
      const newDeal = await DealService.createDeal(deal);
      setDeals([...deals, newDeal]);
    }
    setIsFormVisible(false);
  };

  return (
    <div className={styles.tableContainer}>
      <button onClick={handleAddClick} className={styles.addButton}>Add Deal</button>
      <table>
        <thead>
          <tr>
            <th>Deal</th>
            <th>Stage</th>
            <th>Deal Value</th>
            <th>Expected Close Date</th>
            <th>Close Probability</th>
            <th>Forecast Value</th>
            <th>Contacts</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {deals.map(deal => (
            <tr key={deal._id}>
              <td>{deal.deal_name}</td>
              <td>{deal.stage}</td>
              <td>{deal.deal_value}</td>
              <td>{deal.expected_close_date}</td>
              <td>{deal.close_probability}</td>
              <td>{deal.forecast_value}</td>
              <td>{deal.contact_ids?.map(id => contacts.find(c => c._id === id)?.contact_name).join(', ')}</td>
              <td>
                <button onClick={() => handleEditClick(deal)}>Edit</button>
              </td>
              <td>
                <button className={styles.deleteButton} onClick={() => handleDeleteClick(deal._id!)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isFormVisible && selectedDeal && (
        <DealForm
          deal={selectedDeal}
          contacts={contacts}
          onSave={handleSaveDeal}
          onCancel={() => setIsFormVisible(false)}
        />
      )}
    </div>
  );
};

export default DealsTable;
