// src/components/DealForm.tsx
import React, { useState, useEffect } from 'react';
import { Deal } from '../../types/crm/Deal';
import { Contact } from '../../types/crm/Contact';
import styles from '../../styles/crm/dealform.module.css';

interface DealFormProps {
  deal: Deal;
  contacts: { _id: string; contact_name: string }[]; // Expected contacts type
  onSave: (deal: Deal) => void;
  onCancel: () => void;
}

const DealForm: React.FC<DealFormProps> = ({ deal, contacts, onSave, onCancel }) => {
  const [formData, setFormData] = useState<Deal>(deal);

  useEffect(() => {
    setFormData(deal);
  }, [deal]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSave(formData); // Call the onSave function with the form data
  };

  return (
    <form className={styles.form} onSubmit={handleSubmit}>
      <h2>{deal._id === "undefined" ? "Add Deal" : "Edit Deal"}</h2>
      <label>
        Deal Name:
        <input
          type="text"
          name="deal_name"
          value={formData.deal_name}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Stage:
        <select
          name="stage"
          value={formData.stage}
          onChange={handleChange}
        >
          <option value="new">New</option>
          <option value="in_progress">In Progress</option>
          <option value="closed_won">Closed Won</option>
          <option value="closed_lost">Closed Lost</option>
        </select>
      </label>
      <label>
        Deal Value:
        <input
          type="number"
          name="deal_value"
          value={formData.deal_value}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Expected Close Date:
        <input
          type="date"
          name="expected_close_date"
          value={formData.expected_close_date}
          onChange={handleChange}
        />
      </label>
      <label>
        Close Probability:
        <input
          type="number"
          name="close_probability"
          value={formData.close_probability}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Forecast Value:
        <input
          type="number"
          name="forecast_value"
          value={formData.forecast_value}
          onChange={handleChange}
        />
      </label>
      <label>
        Contact:
        <select
          name="contact_id"
          value={formData.contact_id}
          onChange={handleChange}
          required
        >
          <option value="">Select a contact</option>
          {contacts.map(contact => (
            <option key={contact._id} value={contact._id}>
              {contact.contact_name}
            </option>
          ))}
        </select>
      </label>
      <button type="submit">Save</button>
      <button type="button" onClick={onCancel}>Cancel</button>
    </form>
  );
};

export default DealForm;
