import React, { useState, useEffect } from 'react';
import { Account } from '../../types/crm/Account';
import styles from '../../styles/crm/accountform.module.css';

interface AccountFormProps {
  account: Account;
  onSave: (account: Account) => void;
  onCancel: () => void;
}

const AccountForm: React.FC<AccountFormProps> = ({ account, onSave, onCancel }) => {
  const [formData, setFormData] = useState<Account>({
    _id: account._id || "", // Only set if account has an ID
    account_name: "",
    priority: "medium",
    industry: "",
    description: "",
    number_of_employees: 0,
    org_id: "" // Ensure org_id is included and managed
  });
  
  useEffect(() => {
    if (account._id) {
      setFormData(account);
    } else {
      setFormData({
        _id: "", // Don't assign an invalid _id for new accounts
        account_name: "",
        priority: "medium",
        industry: "",
        description: "",
        number_of_employees: 0,
        org_id: "", // Reset org_id if creating a new account
      });
    }
  }, [account]);


  useEffect(() => {
    if (account._id) {
      setFormData(account);
    } else {
      setFormData({
        _id: "",
        account_name: "",
        priority: "medium",
        industry: "",
        description: "",
        number_of_employees: 0,
        org_id: "", // Reset org_id if creating a new account
      });
    }
  }, [account]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSave(formData); // Pass formData directly; _id and org_id will be handled
  };

  return (
    <div className={styles.popupOverlay}>
      <div className={styles.popup}>
        <form onSubmit={handleSubmit} className={styles.form}>
          <h3>{account._id ? 'Edit Account' : 'Add Account'}</h3>
          <label>
            Account Name:
            <input
              type="text"
              name="account_name"
              value={formData.account_name}
              onChange={handleChange}
              required
            />
          </label>
          <label>
            Priority:
            <select name="priority" value={formData.priority} onChange={handleChange}>
              <option value="high">High</option>
              <option value="medium">Medium</option>
              <option value="low">Low</option>
            </select>
          </label>
          <label>
            Industry:
            <input type="text" name="industry" value={formData.industry} onChange={handleChange} />
          </label>
          <label>
            Description:
            <input type="text" name="description" value={formData.description} onChange={handleChange} />
          </label>
          <label>
            Number of Employees:
            <input
              type="number"
              name="number_of_employees"
              value={formData.number_of_employees}
              onChange={handleChange}
            />
          </label>
          <div className={styles.buttonGroup}>
            <button type="submit">Save</button>
            <button type="button" onClick={onCancel}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AccountForm;
