// src/components/DealsTable.tsx
import React, { useState, useEffect } from 'react';
import { Deal } from '../../types/crm/Deal';
import DealForm from './DealForm';
import {} from '../../services/ContactService'; // Import the contact service
import styles from '../../styles/crm/dealstable.module.css';

interface DealsTableProps {
  orgId: string; // Pass orgId as prop
}

const DealsTable: React.FC<DealsTableProps> = ({ orgId }) => {
  const [deals, setDeals] = useState<Deal[]>([]);
  const [selectedDeal, setSelectedDeal] = useState<Deal | null>(null);
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [contacts, setContacts] = useState([]); // For storing fetched contacts

  useEffect(() => {
    // Fetch contacts from the API when the component mounts
    const fetchContacts = async () => {
      const fetchedContacts = await ContactService.getAllContacts(orgId);
      setContacts(fetchedContacts);
    };

    fetchContacts();
  }, [orgId]);

  const handleAddClick = () => {
    setSelectedDeal({
      _id: undefined,
      deal_name: "",
      stage: "new",
      deal_value: 0,
      expected_close_date: "",
      close_probability: 0,
      forecast_value: 0,
      contact_id: "", // Initialize with empty contact
      org_id,
    });
    setIsFormVisible(true);
  };

  const handleEditClick = (deal: Deal) => {
    setSelectedDeal(deal);
    setIsFormVisible(true);
  };

  const handleDeleteClick = (_id: string) => {
    if (window.confirm("Are you sure you want to delete this deal?")) {
      setDeals(deals.filter(deal => deal._id !== _id));
    }
  };

  const handleSaveDeal = (deal: Deal) => {
    setDeals(prevDeals => {
      if (!deal._id) {
        // Add new deal with a generated _id (if using a backend to handle IDs)
        return [...prevDeals, { ...deal, _id: new Date().toISOString() }]; // Temporary ID
      } else {
        // Update existing deal
        return prevDeals.map(d => (d._id === deal._id ? deal : d));
      }
    });
    setIsFormVisible(false);
  };

  const handleCancel = () => {
    setIsFormVisible(false);
  };

  return (
    <div className={styles.tableContainer}>
      <button onClick={handleAddClick} className={styles.addButton}>Add Deal</button>
      <table>
        <thead>
          <tr>
            <th>Deal Name</th>
            <th>Stage</th>
            <th>Deal Value</th>
            <th>Expected Close Date</th>
            <th>Close Probability</th>
            <th>Forecast Value</th>
            <th>Contact</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {deals.map(deal => (
            <tr key={deal._id}>
              <td>{deal.deal_name}</td>
              <td>{deal.stage}</td>
              <td>{deal.deal_value}</td>
              <td>{deal.expected_close_date}</td>
              <td>{deal.close_probability}</td>
              <td>{deal.forecast_value}</td>
              <td>{deal.contact_id}</td> {/* Display the selected contact's ID */}
              <td>
                <button onClick={() => handleEditClick(deal)}>Edit</button>
              </td>
              <td>
                <button className={styles.deleteButton} onClick={() => handleDeleteClick(deal._id!)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isFormVisible && selectedDeal && (
        <DealForm
          deal={selectedDeal}
          contacts={contacts} // Pass contacts to the form
          onSave={handleSaveDeal}
          onCancel={handleCancel}
        />
      )}
    </div>
  );
};

export default DealsTable;
