// src/components/ContactTable.tsx
import React, { useEffect, useState } from 'react';
import { ContactService } from '../../services/ContactService';
import { Contact } from '../../types/crm/Contact';
import ContactForm from './ContactForm';
import styles from '../../styles/crm/contactstable.module.css';

const ContactTable: React.FC<{ orgId: string }> = ({ orgId }) => {
  const [contacts, setContacts] = useState<Contact[]>([]);
  const [isFormVisible, setFormVisible] = useState(false);
  const [selectedContact, setSelectedContact] = useState<Contact | null>(null);

  useEffect(() => {
    const fetchContacts = async () => {
      const contactsData = await ContactService.getAllContacts(orgId);
      setContacts(contactsData);
    };
    fetchContacts();
  }, [orgId]);

  const handleSave = (contact: Contact) => {
    setContacts((prev) => {
      const existingIndex = prev.findIndex(c => c._id === contact._id);
      if (existingIndex >= 0) {
        const updatedContacts = [...prev];
        updatedContacts[existingIndex] = contact;
        return updatedContacts;
      }
      return [...prev, contact];
    });
  };

  const handleEdit = (contact: Contact) => {
    setSelectedContact(contact);
    setFormVisible(true);
  };

  const handleAddAccount = (contact: Contact) => {
    // Logic to add an account to the contact
    console.log('Add Account functionality to be implemented');
  };

  return (
    <div className={styles.tableContainer}>
      {isFormVisible && (
        <ContactForm
          contact={selectedContact}
          onClose={() => setFormVisible(false)}
          onSave={handleSave}
        />
      )}
      <table>
        <thead>
          <tr>
            <th>Contact Name</th>
            <th>Account</th>
            <th>Deals</th>
            <th>Title</th>
            <th>Priority</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Deals Value</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {contacts.map(contact => (
            <tr key={contact._id}>
              <td>{contact.contact_name}</td>
              <td>
                {contact.account_id ? (
                  contact.account_id
                ) : (
                  <button onClick={() => handleAddAccount(contact)}>Add Account</button>
                )}
              </td>
              <td>{contact.deal_ids?.length || 0}</td>
              <td>{contact.title}</td>
              <td>{contact.priority}</td>
              <td>{contact.phone}</td>
              <td>{contact.email}</td>
              <td>{contact.deal_value ? `$${contact.deal_value.toLocaleString()}` : '-'}</td>
              <td>
                <button onClick={() => handleEdit(contact)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ContactTable;
