import axios from 'axios';
import { Deal } from '../types/crm/Deal';

const API_URL = 'http://localhost:5005/api/deals';

export const getDeals = async (): Promise<Deal[]> => {
  const response = await axios.get(API_URL);
  return response.data;
};

export const createDeal = async (deal: Deal): Promise<Deal> => {
  const response = await axios.post(API_URL, deal);
  return response.data;
};

export const updateDeal = async (id: string, deal: Partial<Deal>): Promise<Deal> => {
  const response = await axios.put(`${API_URL}/${id}`, deal);
  return response.data;
};

export const deleteDeal = async (id: string): Promise<void> => {
  await axios.delete(`${API_URL}/${id}`);
};
